mapper.insert:
SELECT VERIFICATION_SEQ_ID.NEXTVAL FROM DUAL;
INSERT INTO VERIFICATION( VERIFICATION_ID, REPORT_ID, DIVISION_ID, PLAN_ID, SAMPLE_DATA, RESULT_NUMBER, RESULT_BLANK, RESULT_STANDARD, RESULT_CONTROL, RESULT_MIN, RESULT_MAX,
create_date ) VALUES( #{verification_id}, #{report_id}, #{division_id}, #{plan_id}, #{sample_data}, #{result_number}, #{result_blank}, #{result_standard}, #{result_control}, #{result_min}, #{result_max}, sysdate )

mapper.select:
SELECT TO_CHAR(v.create_date , 'YYYY-MM-DD hh24:mm') as create_date , v.*, r.prj_dtl_no, r.prj_dtl_nm, d.division, p.plan_code, g1.genetic crop_list, g2.genetic kind_list, m.method_result FROM verification v LEFT OUTER JOIN report r on r.report_id = v.report_id LEFT OUTER JOIN division d on v.division_id = d.division_id LEFT OUTER JOIN plan p on p.plan_id = v.plan_id LEFT OUTER JOIN plan_seed ps on ps.plan_id = p.plan_id LEFT OUTER JOIN seed s on ps.seed_id = s.seed_id LEFT OUTER JOIN genetic g2 on g2.genetic_id = s.genetic_id LEFT OUTER JOIN genetic g1 on g1.genetic_id = g2.genetic_parents LEFT OUTER JOIN plan_method pm on pm.plan_id = p.plan_id LEFT OUTER JOIN method m on pm.method_id = m.method_id
AND prtcp_mp_dic_code_list LIKE '%'||#{digitalCode}||'%'
ORDER BY v.create_date desc;
