mapper.update:
UPDATE plan SET
    plan_division = #{division_id},
    genetic_id = #{genetic_id}
WHERE plan_id = #{plan_id}

mapper.insert:
INSERT INTO plan_seed(
    plan_seed_id,
    plan_id,
    seed_id
)
VALUES(
    get_seq('plan_seed_seq_id'),
    #{plan_id},
    #{seed_id}
)

mapper.select:
SELECT seed_id
FROM plan_seed
WHERE plan_id = #{plan_id}

mapper.update:
UPDATE plan
SET
    genetic_id = #{genetic_id}
    ,plan_division = #{division_id}
    ,modify_date = sysdate


        ,ATCH_FILE_NM = #{ATCH_FILE_NM}
        ,ORIG_FILE_NM = #{ORIG_FILE_NM}


where plan_id = #{plan_id}

UPDATE PLAN
SET PLAN_PLATE = #{plan_plate},
    PLAN_STEP = #{plan_step}
WHERE plan_id = #{plan_id}

UPDATE PLAN
SET PLAN_STATUS = 1,
    PLAN_CONTENTS = #{comment}
WHERE plan_id = #{plan_id}

UPDATE PLAN
SET PLAN_CONTENTS = #{comment}, PLAN_STATUS = 1
WHERE plan_id = #{plan_id}
